import {Component} from '@angular/core';

@Component({
  selector: 'navbar-cmp',
  templateUrl: 'navbar.component.html'
})
export class NavbarComponent {

  private sidebarVisible: boolean = false;

  constructor() {
  }

  sidebarToggle() {
    const body = document.getElementsByTagName('body')[0];

    if (!this.sidebarVisible) {
      body.classList.add('nav-open');
      this.sidebarVisible = true;
      console.log('making sidebar visible...');
    } else {
      this.sidebarVisible = false;
      body.classList.remove('nav-open');
    }
  }
}
<nav class="navbar navbar-expand-lg navbar-transparent">
  <div class="container-fluid">
    <div class="navbar-header">
      <button class="navbar-toggler" type="button" data-bs-toggle="collapse" aria-expanded="false" aria-label="Toggle navigation" (click)="sidebarToggle()">
        <span class="navbar-toggler-icon"></span>
      </button>
      <a class="navbar-brand" href="#">Flight42</a>
    </div>
    <div class="collapse navbar-collapse">
      <ul class="nav navbar-nav ms-auto">
        <li class="nav-item">
          <a href="#" class="nav-link dropdown-toggle" data-toggle="dropdown">
            <i class="ti-panel"></i>
            <p>Stats</p>
          </a>
        </li>
        <li class="nav-item dropdown">
          <a href="#" class="nav-link dropdown-toggle" id="navbarDropdown" role="button" data-bs-toggle="dropdown" aria-expanded="false">
            <i class="ti-bell"></i>
            <p class="notification">5</p>
            <p>Notifications</p>
          </a>
          <ul class="dropdown-menu" aria-labelledby="navbarDropdown">
            <li><a href="#" class="dropdown-item">Notification 1</a></li>
            <li><a href="#" class="dropdown-item">Notification 2</a></li>
            <li><a href="#" class="dropdown-item">Notification 3</a></li>
            <li><a href="#" class="dropdown-item">Notification 4</a></li>
            <li><hr class="dropdown-divider"></li>
            <li><a href="#" class="dropdown-item">Another notification</a></li>
          </ul>
        </li>
        <li class="nav-item">
          <a href="#" class="nav-link">
            <i class="ti-settings"></i>
            <p>Settings</p>
          </a>
        </li>
      </ul>
    </div>
  </div>
</nav>
