import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { ImmeubleService } from '../immeuble.service';
import { Immeuble } from '../immeuble';
import { map, switchMap } from 'rxjs/operators';
import { of } from 'rxjs';

@Component({
  selector: 'app-immeuble-edit',
  templateUrl: './immeuble-edit.component.html'
})
export class ImmeubleEditComponent implements OnInit {

  id!: string;
  immeuble!: Immeuble;
  feedback: any = {};

  constructor(
    private route: ActivatedRoute,
    private router: Router,
    private immeubleService: ImmeubleService) {
  }

  ngOnInit() {
    this
      .route
      .params
      .pipe(
        map(p => p['id']),
        switchMap(id => {
          if (id === 'new') { return of(new Immeuble()); }
          return this.immeubleService.findById(id);
        })
      )
      .subscribe({
        next: immeuble => {
          this.immeuble = immeuble;
          this.feedback = {};
        },
        error: err => {
          this.feedback = {type: 'warning', message: 'Error loading'};
        }
      });
  }

  save() {
    this.immeubleService.save(this.immeuble).subscribe({
      next: immeuble => {
        this.immeuble = immeuble;
        this.feedback = {type: 'success', message: 'Save was successful!'};
        setTimeout(async () => {
          await this.router.navigate(['/immeubles']);
        }, 1000);
      },
      error: err => {
        this.feedback = {type: 'warning', message: 'Error saving'};
      }
    });
  }

  async cancel() {
    await this.router.navigate(['/immeubles']);
  }
}
<ol class="breadcrumb float-right">
  <li class="breadcrumb-item"><a routerLink="/">Accueil</a></li>
  <li class="breadcrumb-item active">Immeubles</li>
</ol>
<div class="card">
  <div class="header">
    <h2 class="title"> Details</h2>
  </div>
  <div class="content">
    <div *ngIf="feedback.message" class="alert alert-{{feedback.type}}">{{ feedback.message }}</div>
    <form *ngIf="immeuble" #editForm="ngForm" (ngSubmit)="save()">

      <div class="form-group">
        <label>Id</label>
        {{immeuble.id }} 
      </div>

      <div class="form-group">
        <label for="nom">Nom</label>
        <input [(ngModel)]="immeuble.nom" id="nom" name="nom" class="form-control">
      </div>

      <div class="form-group">
        <label for="adresse">Adresse</label>
        <input [(ngModel)]="immeuble.adresse" id="adresse" name="adresse" class="form-control">
      </div>

      <button type="submit" class="btn btn-primary" [disabled]="!editForm.form.valid">Enregistrer</button>
      <button type="button" class="btn btn-secondary" (click)="cancel()">Cancel</button>
    </form>
  </div>
</div>
