import { Component, OnInit } from '@angular/core';
import { CoproprietaireFilter } from '../coproprietaire-filter';
import { CoproprietaireService } from '../coproprietaire.service';
import { Coproprietaire } from '../coproprietaire';

@Component({
  selector: 'app-coproprietaire',
  templateUrl: 'coproprietaire-list.component.html'
})
export class CoproprietaireListComponent implements OnInit {

  filter = new CoproprietaireFilter();
  selectedCoproprietaire!: Coproprietaire;
  feedback: any = {};
  cop:any=[];


  get coproprietaireList(): Coproprietaire[] {
    return this.coproprietaireService.coproprietaireList;
  }

  constructor(private coproprietaireService: CoproprietaireService) {
  }

  ngOnInit() {
    this.search();
  }

  search(): void {
    this.coproprietaireService.load(this.filter);
  }

  select(selected: Coproprietaire): void {
    this.selectedCoproprietaire = selected;
  }

  delete(coproprietaire: Coproprietaire): void {
    if (confirm('Are you sure?')) {
      this.coproprietaireService.delete(coproprietaire).subscribe({
        next: () => {
          this.feedback = {type: 'success', message: 'Delete was successful!'};
          setTimeout(() => {
            this.search();
          }, 1000);
        },
        error: err => {
          this.feedback = {type: 'warning', message: 'Error deleting.'};
        }
      });
    }
  }
}
<ol class="breadcrumb float-right">
  <li class="breadcrumb-item"><a routerLink="/">Home</a></li>
  <li class="breadcrumb-item active">Hotel</li>
</ol>
<div class="card">
  <div class="header">
    <h2 class="title">Hotel List</h2>
  </div>
  <div class="content">
    <form #f="ngForm">

      <button (click)="search()" [disabled]="!f?.valid" class="btn btn-primary">Search</button>
      <a [routerLink]="['../coproprietaires', 'new' ]" class="btn btn-default">New</a>
    </form>
  </div>
</div>
<div class="card" *ngIf="coproprietaireList.length > 0">
  <div *ngIf="feedback.message" class="alert alert-{{feedback.type}}">{{ feedback.message }}</div>
  <div class="table-responsive">
    <table class="table table-centered table-hover mb-0" id="datatable">
      <thead>
        <tr>
          <th class="border-top-0" scope="col">Id</th>
          <th class="border-top-0" scope="col">Nom</th>
          <th class="border-top-0" scope="col">Adresse</th>
          <th class="border-top-0" scope="col" style="width:120px"></th>
        </tr>
      </thead>
      <tbody>
        <tr *ngFor="let item of coproprietaireList" [class.active]="item === selectedCoproprietaire">

          <td>{{item.id}}</td>
          <td>{{item.nom}}</td>
          <td>{{item.adresse}}</td>
          <td style="white-space: nowrap">
            <a [routerLink]="['../coproprietaires', item.id ]" class="btn btn-secondary">Edit</a>&nbsp;
            <button (click)="delete(item)" class="btn btn-danger">Delete</button>
          </td>
        </tr>
      </tbody>
    </table>
  </div>
</div>
