import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { RemiseService } from '../remise.service';
import { Remise } from '../remise';
import { map, switchMap } from 'rxjs/operators';
import { of } from 'rxjs';
import { LotService } from 'src/app/lot/lot.service';

@Component({
  selector: 'app-remise-edit',
  templateUrl: './remise-edit.component.html'
})
export class RemiseEditComponent implements OnInit {


onselectChange($event: any) {
  this.remise.id_lot= $event;
}

  id!: string;
  remise!: Remise;
  feedback: any = {};
  lots: any = [];
  selectedLotId:any;


  constructor(
    private route: ActivatedRoute,
    private router: Router,
    private remiseService: RemiseService,
    private lotService: LotService) {

  }

  ngOnInit() {
    this
      .route
      .params
      .pipe(
        map(p => p['id']),
        switchMap(id => {
          if (id === 'new') { return of(new Remise()); }
          return this.remiseService.findById(id);
        })
      )
      .subscribe({
        next: remise => {
          this.remise = remise;
          this.feedback = {};
          this.loadLots();
        },

        error: err => {
          this.feedback = {type: 'warning', message: 'Error loading'};
        }
      });
  }

  loadLots(){
    this.lotService.getList().subscribe({
      next:(data:any)  => {
        this.lots = data;
      },
      error: (err:any) => {
      console.log (err)     }
    });
  }

  save() {
    this.remise.id_lot=this.selectedLotId;
    console.log(this.remise);
    this.remiseService.save(this.remise).subscribe({

      next: remise => {
        this.remise = remise;

        this.feedback = {type: 'success', message: 'Enregistrement effectué!'};
        setTimeout(async () => {
          await this.router.navigate(['/remises']);
        }, 1000);
      },
      error: err => {
        this.feedback = {type: 'warning', message: 'Erreur enregistrement'};
      }
    });
  }

  async cancel() {
    await this.router.navigate(['/remises']);
  }
}
<ol class="breadcrumb float-right">
  <li class="breadcrumb-item"><a routerLink="/">Accueil</a></li>
  <li class="breadcrumb-item active">Remises</li>
</ol>
<div class="card">
  <div class="header">
    <h2 class="title"> Details</h2>
  </div>
  <div class="content">
    <div *ngIf="feedback.message" class="alert alert-{{feedback.type}}">{{ feedback.message }}</div>
    <form *ngIf="remise" #editForm="ngForm" (ngSubmit)="save()">

      <div class="form-group">
        <label>Id</label>
        {{remise.id || 'n/a'}} 
      </div>

      <!-- <div class="form-group">
        <label for="id_lot">Id lot</label>
        <input type="number" [(ngModel)]="remise.id_lot" id="id_lot" name="id_lot" class="form-control">
      </div> -->
      <div class="form-group">
      <select id="id_lot" (ngModelChange)="onselectChange($event)" class="form-control" [(ngModel)]="selectedLotId">
        <ng-container *ngFor="let lot of lots">
          <option [value]="lot.id">{{lot.nom}}</option>
        </ng-container>
    </select>
  </div>

      <div class="form-group">
        <label for="donneur">Donneur</label>
        <input [(ngModel)]="remise.donneur" id="donneur" name="donneur" class="form-control">
      </div>

      <div class="form-group">
        <label for="receveur">Receveur</label>
        <input [(ngModel)]="remise.receveur" id="receveur" name="receveur" class="form-control">
      </div>

      <div class="form-group">
        <label for="date_remise">Date</label>
        <input type="date" [(ngModel)]="remise.date_remise" id="date_remise" name="date_remise" class="form-control">
      </div>

      <div class="form-group">
        <label for="commentaire">Commentaire</label>
        <input [(ngModel)]="remise.commentaire" id="commentaire" name="commentaire" class="form-control">
      </div>

      <div class="form-group">
        <label for="signature">Signature</label>
        <input [(ngModel)]="remise.signature" id="signature" name="signature" class="form-control">
      </div>

      <div class="form-group">
        <label for="media">Media</label>
        <input [(ngModel)]="remise.media" id="media" name="media" class="form-control">
      </div>

      <button type="submit" class="btn btn-primary" [disabled]="!editForm.form.valid">Save</button>
      <button type="button" class="btn btn-secondary" (click)="cancel()">Cancel</button>
    </form>
  </div>
</div>
