import { Component, OnInit } from '@angular/core';
import { ImmeubleFilter } from '../immeuble-filter';
import { ImmeubleService } from '../immeuble.service';
import { Immeuble } from '../immeuble';

@Component({
  selector: 'app-immeuble',
  templateUrl: 'immeuble-list.component.html'
})
export class ImmeubleListComponent implements OnInit {

  filter = new ImmeubleFilter();
  selectedImmeuble!: Immeuble;
  feedback: any = {};
  immeubleList:any=[];

  // get immeubleList(): Immeuble[] {
  //   return this.immeubleService.immeubleList;
  // }

  constructor(private immeubleService: ImmeubleService) {
  }

  ngOnInit() {
    this.reset();
  }

  search(): void {
    this.immeubleService.load(this.filter);
  }

  reset(): void {
    this.immeubleService.getList().subscribe(

      {next:(data:any) => {
        console.log(data);
        this.immeubleList=data ;
      },
      error:(err:any)=> {
        console.error(err); // Log any errors
      }}
    );
  }

  select(selected: Immeuble): void {
    this.selectedImmeuble = selected;
  }

  delete(immeuble: Immeuble): void {
    if (confirm('Are you sure?')) {
      this.immeubleService.delete(immeuble).subscribe({
        next: () => {
          this.feedback = {type: 'success', message: 'Delete was successful!'};
          setTimeout(() => {
            this.search();
          }, 1000);
        },
        error: err => {
          this.feedback = {type: 'warning', message: 'Error deleting.'};
        }
      });
    }
  }
}
<ol class="breadcrumb float-right">
  <li class="breadcrumb-item"><a routerLink="/">Accueil</a></li>
  <li class="breadcrumb-item active">Immeubles</li>
</ol>
<div class="card">
  <div class="header">
    <h2 class="title">Liste des immeubles</h2>
  </div>
  <div class="content">
    <form #f="ngForm">

      <a [routerLink]="['../immeubles', 'new' ]" class="btn btn-default">Ajouter</a>
    </form>
  </div>
</div>
<div class="card" *ngIf="immeubleList.length > 0">
  <div *ngIf="feedback.message" class="alert alert-{{feedback.type}}">{{ feedback.message }}</div>
  <div class="table-responsive">
    <table class="table table-centered table-hover mb-0" id="datatable">
      <thead>
        <tr>
          <th class="border-top-0" scope="col">Id</th>
          <th class="border-top-0" scope="col">Nom</th>
          <th class="border-top-0" scope="col">Adresse</th>
          <th class="border-top-0" scope="col" style="width:120px"></th>
        </tr>
      </thead>
      <tbody>
        <tr *ngFor="let item of immeubleList" [class.active]="item === selectedImmeuble">

          <td>{{item.id}}</td>
          <td>{{item.nom}}</td>
          <td>{{item.adresse}}</td>
          <td style="white-space: nowrap">
            <a [routerLink]="['../immeubles', item.id ]" class="btn btn-secondary">Modifier</a>&nbsp;
            <button (click)="delete(item)" class="btn btn-danger">Supprimer</button>
          </td>
        </tr>
      </tbody>
    </table>
  </div>
</div>
