import { Component, OnInit } from '@angular/core';
import { RemiseFilter } from '../remise-filter';
import { RemiseService } from '../remise.service';
import { Remise } from '../remise';

@Component({
  selector: 'app-remise',
  templateUrl: 'remise-list.component.html'
})
export class RemiseListComponent implements OnInit {

  filter = new RemiseFilter();
  selectedRemise!: Remise;
  feedback: any = {};
  remiseList: any = [];


  // get remiseList(): Remise[] {
  //   return this.remiseService.remiseList;
  // }

  constructor(private remiseService: RemiseService) {
  }

  ngOnInit() {
    this.reset();

  }

  search(): void {
    this.remiseService.load(this.filter);
  }
  // reset(): void {
  //   this.remiseService.getList();
  // }
  reset(): void {
    this.remiseService.getList().subscribe(

      {next:(data:any) => {
        console.log(data);
        this.remiseList=data ;
      },
      error:(err:any)=> {
        console.error(err); // Log any errors
      }}
    );
  }

  select(selected: Remise): void {
    this.selectedRemise = selected;
  }

  delete(remise: Remise): void {
    if (confirm('Are you sure?')) {
      this.remiseService.delete(remise).subscribe({
        next: () => {
          this.feedback = {type: 'success', message: 'Delete was successful!'};
          setTimeout(() => {
            this.search();
          }, 1000);
        },
        error: err => {
          this.feedback = {type: 'warning', message: 'Error deleting.'};
        }
      });
    }
  }
}
<ol class="breadcrumb float-right">
  <li class="breadcrumb-item"><a routerLink="/">Accueil</a></li>
  <li class="breadcrumb-item active">Remises</li>
</ol>
<div class="card">
  <div class="header">
    <h2 class="title">Liste des remises effectuées</h2>
  </div>
  <div class="content">
    <form #f="ngForm">


      <a [routerLink]="['../remises', 'new' ]" class="btn btn-default">Ajouter</a>
    </form>
  </div>
</div>
<div class="card" *ngIf="remiseList.length > 0">
  <div *ngIf="feedback.message" class="alert alert-{{feedback.type}}">{{ feedback.message }}</div>
  <div class="table-responsive">
    <table class="table table-centered table-hover mb-0" id="datatable">
      <thead>
        <tr>
          <th class="border-top-0" scope="col">Id</th>
          <th class="border-top-0" scope="col">Lot</th>
          <th class="border-top-0" scope="col">Donneur</th>
          <th class="border-top-0" scope="col">Receveur</th>
          <th class="border-top-0" scope="col">Date</th>
          <th class="border-top-0" scope="col">Commentaire</th>
          <th class="border-top-0" scope="col">Signature</th>
          <th class="border-top-0" scope="col">Media</th>
          <th class="border-top-0" scope="col" style="width:120px"></th>
        </tr>
      </thead>
      <tbody>
        <tr *ngFor="let item of remiseList" [class.active]="item === selectedRemise">

          <td>{{item.id}}</td>
          <td>{{item.id_lot}}</td>
          <td>{{item.donneur}}</td>
          <td>{{item.receveur}}</td>
          <td>{{item.date_remise | date}}</td>
          <td>{{item.commentaire}}</td>
          <td>{{item.signature}}</td>
          <td>{{item.media}}</td>
          <td style="white-space: nowrap">
            <a [routerLink]="['../remises', item.id ]" class="btn btn-secondary">Modifier</a>&nbsp;
            <button (click)="delete(item)" class="btn btn-danger">Supprimer</button>
          </td>
        </tr>
      </tbody>
    </table>
  </div>
</div>
